mapper.insert:
	INSERT
	INTO
		TB_BOARD
	(
		BOARD_TITLE
	,   BOARD_CONTENTS
	,   BOARD_REG_SEQ
	,   BOARD_REG_DATE
	)
	VALUES
    (
		    #{boardTitle}
		  , #{boardContents}
		  , #{boardRegSeq}
		  , NOW()
    )

mapper.update:
UPDATE
	TB_BOARD
SET
	 BOARD_SEQ = #{boardSeq}
	,BOARD_TITLE =  #{boardTitle}
	,BOARD_CONTENTS = #{boardContents}
	,BOARD_REG_SEQ = #{boardRegSeq}
	,BOARD_MDF_DATE = NOW()
WHERE
	BOARD_SEQ = #{boardSeq}
