mapper.insert:
INSERT
  INTO TB_USER
(
  USER_LOGIN
, USER_ID
, USER_PW
, USER_NAME
, USER_EMAIL
, USER_LEVEL
, USER_AGE
, USER_GENDER
, USER_NICKNAME
, USER_STATUS
, JOIN_DT
, USER_PW_NEW
, USER_PLAYER
)
VALUES
(
  1
, #{userId}
, #{userPw}
, #{userName}
, #{userEmail}
, #{userLevel}
, #{userAge}
, #{userGender}
, #{userNickname}
, 'Y'
, NOW()
, #{userPwNew}
, #{userPlayer}
)

mapper.update:
UPDATE
    TB_USER
SET
      USER_NAME = #{userName}
    , USER_EMAIL = #{userEmail}
    , USER_NICKNAME = #{userNickname}
    , USER_GENDER = #{userGender}
    , USER_LEVEL = #{userLevel}
    , USER_PLAYER = #{userPlayer}
WHERE USER_SEQ = #{userSeq}

UPDATE
    TB_USER
SET
    USER_PW = #{userPwNew}
WHERE USER_SEQ = #{userSeq}

UPDATE
    TB_USER
SET
    USER_PW = #{userPwNew}
WHERE USER_ID = #{userId}
